con:config.script:
import org.apache.poi.ss.usermodel.*
import org.apache.poi.hssf.usermodel.*
import org.apache.poi.ss.util.*
import groovy.json.JsonSlurper
import com.eviware.soapui.support.types.StringToStringMap 


//Global variables
def stopAtLine = 1 //0 = run all
def excelName = "Testcases-Brand-Quote"
date = new Date().format( 'yyyyMMdd' )
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
projectPath = groovyUtils.projectPath
log.info "Detected project path: " + projectPath
def variables = testRunner.testCase.getTestStepByName("Variables")
def tCaseRun = testRunner.testCase.testSuite.testCases["QUOTE"]
def fail = false
errorLog = []



// Initiate ENVIRONMENT
	//com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "Envir", "TEST" )
def environment = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("Envir")
def headers = new StringToStringMap()
headers.put("Content-type", "application/json");
headers.put("Cache-Control", "no-cache");

if(environment.equals("TST")){
headers.put("Ocp-Apim-Subscription-Key", "5c6ebe49aaa94d02aae2d3e8b018128e");
testRunner.testCase.testSuite.project.setPropertyValue("BaseUrl", "https://test.api.coronadirect.be")	
}
else if(environment.equals("ACC")){
headers.put("Ocp-Apim-Subscription-Key", "62196e7c7e464804bc2deefe3552bb1a");
testRunner.testCase.testSuite.project.setPropertyValue("BaseUrl", "https://acc.api.coronadirect.be")	
}
tCaseRun.getTestStepByName("Quote").httpRequest.requestHeaders = headers



//Initiate external Functions.groovy
GroovyScriptEngine gse = new GroovyScriptEngine(projectPath)
externalScript = gse.loadScriptByName("Functions.groovy")  
Functions = externalScript.newInstance(context: context, log: log, testRunner: testRunner)
Functions.printCall()


// Initiate EXCEL
def inputStream = new FileInputStream(projectPath+"\\" + excelName + ".xls");
HSSFWorkbook wb = new HSSFWorkbook(inputStream);
HSSFSheet ws = wb.getSheet("TestCases")  
def totalRows = ws.getPhysicalNumberOfRows()
def totalColumns = ws.getRow(0).getPhysicalNumberOfCells()

// Initiate LOG dirs
Functions.checkAndCreateFolder("Requests", projectPath, date)
Functions.checkAndCreateFolder("Fails", projectPath, date)


// Iterate rows / TESTCASES
for(def i=1; i<totalRows; i++){
	// Reset variables
	def request = ""
	def response = ""
	errorLog.clear()
	fail = false
	def row = ws.getRow(i)

	//Populate variables with data from excel
	for (var in variables.getPropertyList()){
		//correct column of given row is selected using the function 'getColumn()
		Cell cell = row.getCell(Functions.getColumn(var.getName(), ws, totalColumns))

		//the variable name is extracted from property list. Value from cell is extracted with function
		variables.setPropertyValue(var.getName(),Functions.getCellValue(cell))
	 }

//-- REQUEST --
	//API call .run()
	def tStep = tCaseRun.getTestStepByName("Quote")
	def runner = tStep.run(testRunner, context)

	//REQUEST being send
	request = runner.getTestStep().getPropertyValue('RawRequest')

	//Create requestLog
	def requestLog = new File(projectPath + "\\Requests\\" + date + "\\row " + (i+1) +".txt");
	requestLog.write (request);


// -- RESPONSE --
	def httpResponseHeaders = runner.getTestStep().testRequest.response.responseHeaders
	def httpStatus = httpResponseHeaders["#status#"]
	def httpStatusCode = (httpStatus =~ "[1-5]\\d\\d")[0]


	//if JSON is not correct, statusCode will be 500
	if (httpStatusCode != "500"){
		response = runner.getTestStep().getPropertyValue('Response')
		def jsonSlurper = new JsonSlurper()
		def object = jsonSlurper.parseText(response)

// -- ASSERTIONS --
			//Check if server accepted the request, or if the returnCode is as expected
		try {
	 		assert object[0].control.applicationReturnCode == variables.getPropertyValue("returnCode")
		}
		catch (AssertionError e) {
			fail = true
			errorLog.addAll(Functions.createErrorLogText("ApplicationReturnCode not "+ variables.getPropertyValue("returnCode")+" \n" + "errorMessage: " + object[0].control.errorMessage ,variables.getPropertyValue("returnCode"),object[0].control.applicationReturnCode))
		}
		//only if the returnCode is 00000, the following assertions can be done
		if (object[0].control.applicationReturnCode == '00000'){

			//Check if BD - Building is as expected
			try {
		 		assert Functions.checkValues(object[0].results.coverages[0].grossPremium.toString(), variables.getPropertyValue("BD"))
			}
			catch (AssertionError e) {
				fail = true
				errorLog.addAll(Functions.createErrorLogText("BD - Building premium FAIL",variables.getPropertyValue("BD"),object[0].results.coverages[0].grossPremium))
			}	

			//Check if CO / Contents is as expected
			try {
		 		assert Functions.checkValues(object[0].results.coverages[1].grossPremium.toString(), variables.getPropertyValue("CO"))
			}
			catch (AssertionError e) {
				fail = true
				errorLog.addAll(Functions.createErrorLogText("CO / Contents premium FAIL",variables.getPropertyValue("CO"),object[0].results.coverages[1].grossPremium))
			}	

			//Check if CT / ContentsTheft is as expected
			try {
		 		assert Functions.checkValues(object[0].results.coverages[2].grossPremium.toString(), variables.getPropertyValue("CT"))
			}
			catch (AssertionError e) {
				fail = true
				errorLog.addAll(Functions.createErrorLogText("CT / ContentsTheft premium FAIL",variables.getPropertyValue("CT"),object[0].results.coverages[2].grossPremium))
			}	

			//Check if LA / LegalAssistance is as expected
			try {
		 		assert Functions.checkValues(object[0].results.coverages[3].grossPremium.toString(), variables.getPropertyValue("LA"))
			}
			catch (AssertionError e) {
				fail = true
				errorLog.addAll(Functions.createErrorLogText("LA / LegalAssistance premium FAIL",variables.getPropertyValue("LA"),object[0].results.coverages[3].grossPremium))
			}	
			//Check if AS / Assistance is as expected
			try {
		 		assert Functions.checkValues(object[0].results.coverages[4].grossPremium.toString(), variables.getPropertyValue("AS"))
			}
			catch (AssertionError e) {
				fail = true
				errorLog.addAll(Functions.createErrorLogText("AS / Assistance premium FAIL",variables.getPropertyValue("AS"),object[0].results.coverages[4].grossPremium))
			}	
		}
	}

// -- CREATE LOGS --
	// When httpStatusCode == 500:
	else{ 
		fail = true
		errorLog << "JSON error"
		errorLog << httpStatus
		Functions.errorLines << "FAIL line " + (i+1) + " : JSON error \n"
	}

	//Create and write .txt logs
	if (fail){
		Functions.rowFailed(errorLog, i, projectPath, date, row);
	}
	else{
		Functions.rowPassed(i, row);
	}

	//stop loop
	if(i == stopAtLine){
		break;
	}
}

// -- AFTER ALL --
Functions.finishTestSuite(excelName, projectPath, wb);

